mapper.select:
SELECT FRMNO, FRMNAME, FRMDESC, FRMCONTENTS
FROM SGN_FORM
WHERE FRMNO = #{frmno}

mapper.update:
UPDATE SGN_FORM
SET FRMNAME = #{frmname}
  , FRMDESC = #{frmdesc}
  , FRMCONTENTS = #{frmcontents}
WHERE FRMNO = #{frmno}

mapper.delete:
UPDATE SGN_FORM
SET DELETEFLAG = 'Y'
WHERE FRMNO = #{frmno}
